import React from "react";
import styled from "styled-components";

const Wrapper = styled.section`
  width: 860px;
  height: 350px;
  border-radius: 20px;
  box-shadow: 0 3px 20px 0 rgba(69, 71, 101, 0.06);
  background-color: white;
  padding-top: 37px;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  justify-content: center;
  & > .title {
    font-size: 20px;
    font-weight: bold;
    color: #191f28;
  }
  & > .sub {
    margin-top: 11px;
    margin-bottom: 21px;
    font-size: 13px;
    font-weight: 500;
    line-height: 1.54;
    color: #191f28;
  }
  & > .input-wrapper {
    width: 544px;
    height: 54px;
    border: solid 1px #dbdbdb;
    border-radius: 10px;
    background-color: #f7f8fa;
  }
`;

function Email() {
  return (
    <Wrapper>
      <div className="title">이메일 인증 후 다운로드</div>
      <div className="sub">
        메일을 입력 후 [인증메일 요청]을 클릭하시면 작성해주신 메일로 인증
        메일이 발송됩니다. <br /> 메일 인증을 마무리하시면 다운로드 링크가
        오픈됩니다 ☺️
      </div>
      <div className="input-wrapper">
        <div className="left">
          <figure>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="26.905"
              height="21.524"
              viewBox="0 0 26.905 21.524"
            >
              <path
                d="M27.214 6H5.69a2.687 2.687 0 0 0-2.677 2.69L3 24.833a2.7 2.7 0 0 0 2.69 2.69h21.524a2.7 2.7 0 0 0 2.69-2.69V8.69A2.7 2.7 0 0 0 27.214 6zm0 5.381-10.762 6.726L5.69 11.381V8.69l10.762 6.726L27.214 8.69z"
                transform="translate(-3 -6)"
                style={{ fill: "#dbdbdb" }}
              />
            </svg>
          </figure>
          <div className="tag">이메일</div>
        </div>
      </div>
    </Wrapper>
  );
}

export default Email;
